VALUES (0, 'Alerte - Preprod', '', '<p>{source}<span style="font-family: courier new, courier, monospace;">&lt;div class="alerte-message-container" style="padding: 8px; background: #DB333E; border-radius: 4px; position: absolute; width: 80vw; top: 25px; z-index: 12; left: 10vw;"&gt;<br /><img src="media/sourcerer/images/tab.png" />&lt;p style="font-weight: 500; color: #fff;"&gt;<br /><img src="media/sourcerer/images/tab.png" /><img src="media/sourcerer/images/tab.png" />&lt;span style="font-size: 16pt;"&gt;Cette plateforme de préproduction est une copie de la production datant du 12/07/2022. Les mails sont désactivés. Elle est isolée du web.&lt;/span&gt;<br /><img src="media/sourcerer/images/tab.png" /> &lt;/p&gt;<br /><img src="media/sourcerer/images/tab.png" /> &lt;span id="close-preprod-alerte-container" class="material-icons em-pointer" style="color:white;position:absolute;top:10px;right:5px;"&gt;close&lt;/span&gt;<br />&lt;/div&gt;<br /><br />&lt;script&gt;<br /><img src="media/sourcerer/images/tab.png" />document.addEventListener(''click'', (event) =&gt; {<br /><img src="media/sourcerer/images/tab.png" /> if (event.target.id === ''close-preprod-alerte-container'') {<br /><img src="media/sourcerer/images/tab.png" /><img src="media/sourcerer/images/tab.png" />document.querySelector(''.alerte-message-container'').classList.add(''hidden'');<br /><img src="media/sourcerer/images/tab.png" /> }<br /><img src="media/sourcerer/images/tab.png" />});<br /><img src="media/sourcerer/images/tab.png" /><br />&lt;/script&gt;</span>{/source}</p>', 1, 'header-b', 0, '2022-02-22 16:28:57', '2022-02-22 16:28:57', '2099-02-22 16:28:57', 0, 'mod_custom', 1, 0, '{"prepare_content":0,"backgroundimage":"","layout":"_:default","moduleclass_sfx":"banner-preprod","cache":1,"cache_time":900,"cachemode":"static","module_tag":"div","bootstrap_size":"0","header_tag":"h3","header_class":"","style":"0"}', 0, '*');
SET @module_id = LAST_INSERT_ID();

