SET autocommit = 0;

START TRANSACTION;

CREATE OR REPLACE VIEW `jos_emundus_stats_candidature_create`  AS  select count(`jos_emundus_campaign_candidature`.`fnum`) AS `countCandidature`,cast(`jos_emundus_campaign_candidature`.`date_time` as date) AS `date`,`jos_emundus_setup_campaigns`.`label` AS `campaign` from (`jos_emundus_campaign_candidature` left join `jos_emundus_setup_campaigns` on(`jos_emundus_campaign_candidature`.`campaign_id` = `jos_emundus_setup_campaigns`.`id`)) group by cast(`jos_emundus_campaign_candidature`.`date_time` as date) order by cast(`jos_emundus_campaign_candidature`.`date_time` as date) ;

CREATE OR REPLACE VIEW `jos_emundus_stats_candidature_submit`  AS  select count(`jos_emundus_campaign_candidature`.`fnum`) AS `countCandidature`,cast(`jos_emundus_campaign_candidature`.`date_submitted` as date) AS `date`,`jos_emundus_setup_campaigns`.`label` AS `campaign` from (`jos_emundus_campaign_candidature` left join `jos_emundus_setup_campaigns` on(`jos_emundus_campaign_candidature`.`campaign_id` = `jos_emundus_setup_campaigns`.`id`)) where cast(`jos_emundus_campaign_candidature`.`date_submitted` as date) <> '0000-00-00' group by cast(`jos_emundus_campaign_candidature`.`date_submitted` as date) ;

CREATE OR REPLACE VIEW `jos_emundus_stats_files_graph`  AS  select `ecc`.`id` AS `id`,count(distinct `ecc`.`fnum`) AS `nb`,`esc`.`year` AS `schoolyear`,`esc`.`label` AS `campaign`,`esc`.`training` AS `course`,`ecc`.`submitted` AS `submitted`,`ecc`.`status` AS `status`,`ess`.`value` AS `value`,`ecc`.`campaign_id` AS `campaign_id`,`ecc`.`published` AS `published` from (((`jos_emundus_campaign_candidature` `ecc` left join `jos_emundus_setup_campaigns` `esc` on(`esc`.`id` = `ecc`.`campaign_id`)) left join `jos_emundus_setup_status` `ess` on(`ess`.`step` = `ecc`.`status`)) left join `jos_users` `u` on(`u`.`id` = `ecc`.`user_id`)) group by `ecc`.`campaign_id`,`ecc`.`status`,`ecc`.`id` ;

CREATE OR REPLACE VIEW `jos_emundus_stats_nombre_comptes`  AS  select uuid() AS `id`,count(`eu`.`profile`) AS `nombre`,date_format(`eu`.`registerDate`,'%Y%m%d') AS `_date`,date_format(`eu`.`registerDate`,'%Y-%m-%d') AS `_day`,date_format(`eu`.`registerDate`,'%u') AS `_week`,date_format(`eu`.`registerDate`,'%b') AS `_month`,date_format(`eu`.`registerDate`,'%Y') AS `_year`,`sp`.`id` AS `profile_id`,`sp`.`label` AS `profile_label` from (`jos_emundus_users` `eu` left join `jos_emundus_setup_profiles` `sp` on(`sp`.`id` = `eu`.`profile`)) where `eu`.`profile` in (select `jos_emundus_setup_profiles`.`id` from `jos_emundus_setup_profiles` where `jos_emundus_setup_profiles`.`published` = 1) group by `eu`.`profile`,date_format(`eu`.`registerDate`,'%Y%m%d') ;

