/* CREATE TABLES */
create table jos_emundus_qcm_applicants
(
    id int auto_increment
        primary key,
    date_time datetime default curdate() null,
    fnum varchar(255) null,
    user int null,
    questions varchar(255) null,
    qcmid int null,
    step int null,
    pending varchar(255) null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create table jos_emundus_qcm_questions
(
    id int auto_increment
        primary key,
    date_time datetime null,
    section int null,
    code varchar(255) null,
    question mediumtext null,
    proposals varchar(255) null,
    time int null,
    answers varchar(255) null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create table jos_emundus_qcm_questions_765_repeat
(
    id int auto_increment
        primary key,
    parent_id int null,
    proposals varchar(255) null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create index fb_parent_fk_parent_id_INDEX
    on jos_emundus_qcm_questions_765_repeat (parent_id);

create table jos_emundus_qcm_section
(
    id int auto_increment
        primary key,
    date_time datetime null,
    name varchar(255) null,
    section int null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create table jos_emundus_setup_qcm
(
    id int auto_increment
        primary key,
    date_time datetime null,
    name varchar(255) null,
    form_id int(4) null,
    count int null,
    group_id int(4) null,
    type_choices text null,
    questionid int null,
    sectionid int null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create table jos_emundus_setup_qcm_repeat_questionid
(
    id int auto_increment
        primary key,
    parent_id int null,
    questionid int null,
    params text null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create index fb_parent_fk_parent_id_INDEX
    on jos_emundus_setup_qcm_repeat_questionid (parent_id);

create index fb_repeat_el_questionid_INDEX
    on jos_emundus_setup_qcm_repeat_questionid (questionid);

create table jos_emundus_setup_qcm_repeat_sectionid
(
    id int auto_increment
        primary key,
    parent_id int null,
    sectionid int null,
    params text null
) ENGINE=InnoDB DEFAULT CHARSET=utf8mb4 DEFAULT COLLATE=utf8mb4_unicode_ci;

create index fb_parent_fk_parent_id_INDEX
    on jos_emundus_setup_qcm_repeat_sectionid (parent_id);

create index fb_repeat_el_sectionid_INDEX
    on jos_emundus_setup_qcm_repeat_sectionid (sectionid);
