(1, 'intro_text', ''),
(2, 'registration_form_message', '<p>Please enter information in the form below to process registration for event <strong>[EVENT_TITLE]</strong>.</p>'),
(3, 'registration_form_message_group', '<p>Please enter information in the form below to complete group registration for event <strong>[EVENT_TITLE]</strong>.</p>'),
(4, 'number_members_form_message', '<p>Please enter number of members for your group registration. Number of members need to be greater than or equal 2. You can enter detail information of these members in the next step.</p>'),
(5, 'member_information_form_message', '<p>Please enter information of this member in the form below . All fields marked with (<span class="required">*</span>) is required .</p>'),
(6, 'thanks_message', '<p>Thanks for registering for event <strong>[EVENT_TITLE]</strong>. Your registration detail is as follow :</p>\r\n<p>[REGISTRATION_DETAIL]</p>\r\n<p>Regards,</p>\r\n<p>Events management Team</p>'),
(7, 'thanks_message_offline', '<p>Thanks for registering for event <strong>[EVENT_TITLE]</strong>. Your registration detail is as follow :</p>\r\n<p>[REGISTRATION_DETAIL]</p>\r\n<p>Please send the offline payment via our bank account . Information of our bank account is as follow :</p>\r\n<p>Account Holder Name, Bank Name, Account Number XXXYYYZZZZ</p>\r\n<p>If we don''t receive your payment within 3 days, we will cancel the registration and you cannot attend to this event .</p>\r\n<p>Regards,</p>\r\n<p>Events management Team</p>'),
(8, 'cancel_message', '<p>Your registration for event [EVENT_TITLE] was cancelled.</p>'),
(9, 'registration_cancel_message_free', '<p>You have just cancel your registration for event [EVENT_TITLE]</p>\r\n<p>Thanks,</p>\r\n<p>Event Registration Team</p>'),
(10, 'registration_cancel_message_paid', '<p>Your registration for event <strong>[EVENT_TITLE]</strong> has successfully cancelled. Our event registration team will check your registration and process the refund within 24 hours from now .</p>\r\n<p>Thanks,</p>\r\n<p>Registration Team</p>\r\n<p> </p>'),
(11, 'user_registration_cancel_subject', 'Your registration for event [EVENT_TITLE] was cancelled'),
(12, 'user_registration_cancel_message', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>We want to inform you want your registration for event <strong>[EVENT_TITLE] </strong>was cancelled by administrator</p>\r\n<p>Regards,</p>\r\n<p>Administrator Team</p>'),
(13, 'admin_email_subject', 'New Registration For Event : [EVENT_TITLE]'),
(14, 'admin_email_body', '<p>Dear administrator</p>\r\n<p>User [FIRST_NAME] [LAST_NAME] has just registered for event <strong>[EVENT_TITLE]</strong>. The registration detail is as follow :</p>\r\n<p>[REGISTRATION_DETAIL]</p>\r\n<p>Regards,</p>\r\n<p>Events management team</p>\r\n<p> </p>'),
(15, 'user_email_subject', 'Event registration confirmation'),
(16, 'user_email_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>You have just registered for event <strong>[EVENT_TITLE]</strong>. The registration detail is as follow :</p>\r\n<p>[REGISTRATION_DETAIL]</p>\r\n<p>Regards,</p>\r\n<p>Events management Team</p>'),
(17, 'user_email_body_offline', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>You have just registered for event <strong>[EVENT_TITLE]</strong>. The registration detail is as follow :</p>\r\n<p>[REGISTRATION_DETAIL] .</p>\r\n<p>Please send the offline payment via our bank account . Information of our bank account is as follow :</p>\r\n<p>Account Holder Name, Bank Name, Account Number XXXYYYZZZZ</p>\r\n<p>If we don''t receive your payment within 3 days, we will cancel the registration and you cannot attend to this event .</p>\r\n<p>Regards,</p>\r\n<p>Events management Team</p>'),
(18, 'group_member_email_subject', 'Event registration confirmation'),
(19, 'group_member_email_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>You have just been registered for event <strong>[EVENT_TITLE] </strong>as a group member. Your registration details is as follow:</p>\r\n<p>[MEMBER_DETAIL]</p>\r\n<p>Regards,</p>\r\n<p>Events management Team</p>'),
(20, 'reminder_email_subject', 'Reminder for event [EVENT_TITLE]'),
(21, 'reminder_email_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>This email is used to remind you that you have registered for event [EVENT_TITLE]. The event will occur on <strong>[EVENT_DATE]</strong>, so please come and attend the event on time.</p>\r\n<p>Regards,</p>\r\n<p>Website administrator tea</p>'),
(22, 'registration_cancel_email_subject', 'Registration Cancel for even [EVENT_TITLE]'),
(23, 'registration_cancel_email_body', '<p>Dear administrator</p>\r\n<p>User <strong>[FIRST_NAME] [LAST_NAME]</strong> has just cancel their registration for event <strong>[EVENT_TITLE]</strong> . You can login to back-end of your site to see the detail and process the refund if needed .</p>\r\n<p>Regards,</p>\r\n<p>Administrator Team</p>'),
(24, 'registration_approved_email_subject', 'Your registration for event [EVENT_TITLE] approved'),
(25, 'registration_approved_email_body', '<p>Dear [FIRST_NAME] [LAST_NAME]</p>\r\n<p>Your registration for event [EVENT_TITLE] has just approved by our administrator team. Your registration details is as follow:</p>\r\n<p>[REGISTRATION_DETAIL]</p>\r\n<p>Regards,</p>\r\n<p>Website Administrator Team</p>'),
(26, 'submit_event_user_email_subject', 'Event submission confirmation'),
(27, 'submit_event_user_email_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>We received your event submission <strong>[EVENT_TITLE] [EVENT_DATE]</strong>. Our administrators will check and approve your submission ASAP</p>\r\n<p>Regards,</p>\r\n<p>Website administrator team</p>\r\n<p> </p>'),
(28, 'submit_event_admin_email_subject', 'New event subission received'),
(29, 'submit_event_admin_email_body', '<p>Dear administrator</p>\r\n<p>User <strong>[NAME]</strong> (<strong>[USERNAME]</strong>) has just submitted event <strong>[EVENT_TILE]</strong> from frontend of your site. Please login to administrator area to check and approve the event. You can click on the link below to access to see the submission details</p>\r\n<p>[EVENT_LINK]</p>\r\n<p>Regards,</p>\r\n<p>Website administrator team</p>'),
(30, 'invitation_form_message', '<p>Please enter information in the form below to send invitation to your friends to invite them to register for the event <strong>[EVENT_TITLE]</strong></p>'),
(31, 'invitation_complete', '<p>The invitation was sent to your friends. Thank you !</p>'),
(32, 'invitation_email_subject', 'Invitation to register for event [EVENT_TITLE]'),
(33, 'invitation_email_body', '<p>Dear <strong>[NAME]</strong></p>\r\n<p>Your friend <strong>[SENDER_NAME]</strong> has suggested you to view and register for the event <strong>[EVENT_TITLE]</strong> in our site. Please access to <strong>[EVENT_DETAIL_LINK]</strong> to view and register for the event.</p>\r\n<p>Note from [SENDER_NAME] :</p>\r\n<p><em>[PERSONAL_MESSAGE]</em></p>\r\n<p>Regards,</p>\r\n<p>Events manager team</p>\r\n<p> </p>'),
(34, 'waitinglist_form_message', '<p>This event is full at the moment. Please enter information in the form below to join waiting list for event <strong>[EVENT_TITLE]</strong>.  If someone cancel their registration for the event, we will inform you so that you can join this event.</p>'),
(35, 'waitinglist_complete_message', '<p>Thanks for joiing waitinglist of event <strong>[EVENT_TITLE] . </strong>We will email you if there is anyone cancel their registration.</p>'),
(36, 'watinglist_confirmation_subject', 'Waitinglist confirmation'),
(37, 'watinglist_confirmation_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>Thanks for joining waitinglist of our event [EVENT_TITLE] . We will inform you as if there is someone cancel their registration and you can attend the event.</p>\r\n<p>Regards,</p>\r\n<p>Events management team</p>'),
(38, 'watinglist_notification_subject', 'Waitinglist Notification'),
(39, 'watinglist_notification_body', '<p>Dear Administrator</p>\r\n<p>User <strong>[FIRST_NAME] [LAST_NAME] </strong>has just joined waitinglist for event <strong>[EVENT_TITLE] . </strong></p>\r\n<p>Regards,</p>\r\n<p>Events management team</p>'),
(40, 'registrant_waitinglist_notification_subject', 'Someone cancel the registration'),
(41, 'registrant_waitinglist_notification_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>The registrant <strong>[REGISTRANT_FIRST_NAME] [REGISTRANT_LAST_NAME]</strong> has just cancelled his registration for the event <strong>[EVENT_TITLE]</strong></p>\r\n<p>If you still want to attend to this event, please click on the link below to view and register for the event.</p>\r\n<p><strong>[EVENT_LINK]</strong></p>\r\n<p>Regards,</p>\r\n<p>Website Administrator Team</p>'),
(42, 'request_payment_email_subject', 'Please make payment for your registration [ID]'),
(43, 'request_payment_email_body', '<p>Dear [FIRST_NAME] [LAST_NAME]</p>\r\n<p>Your joined waiting list for our event [EVENT_TITLE]. Recently, one of our registrant cancelled their registration, so we happy to inform that you can make payment to become registrant of the event. Please click on the link below to process payment</p>\r\n<p>[PAYMENT_LINK]</p>\r\n<p>If we don''t receive payment within one day, we will give it to the next waiting list user</p>\r\n<p>Regards,</p>\r\n<p>Website administrator team</p>'),
(44, 'registration_payment_form_message', '<p>Please enter information in the form below to process payment for registration [REGISTRATION_ID] of the event [EVENT_TITLE]. After payment completes, you will become registrant of this event</p>\r\n<p> </p>'),
(45, 'deposit_payment_form_message', '<p>Please enter information in the form below to complete payment your registration <strong>#[REGISTRATION_ID]</strong>. The amount you have to pay is <strong>[AMOUNT]</strong></p>'),
(46, 'deposit_payment_thanks_message', '<p>Thanks for complete payment for your registration #<strong>[REGISTRATION_ID]</strong>. The amount you paid is <strong>[AMOUNT] </strong>and the Transaction ID is <strong>[TRANSACTION_ID]</strong></p>\r\n<p>Regards,</p>\r\n<p>Website administrator team</p>'),
(47, 'deposit_payment_admin_email_subject', 'New Deposit payment for registration #[REGISTRATION_ID]'),
(48, 'deposit_payment_admin_email_body', '<p style="font-size: 12.16px; line-height: 15.808px;">Dear administrator</p>\r\n<p style="font-size: 12.16px; line-height: 15.808px;">Registrant <strong>[FIRST_NAME] [LAST_NAME]</strong> has just completed payment for the registration record <strong>#[REGISTRATION_ID] </strong>using [PAYMENT_METHOD] payment method. The payment amount is <strong>[AMOUNT] </strong>and the Transaction ID is <strong>[TRANSACTION_ID]</strong></p>\r\n<p style="font-size: 12.16px; line-height: 15.808px;">Regards,</p>\r\n<p style="font-size: 12.16px; line-height: 15.808px;">Website administrator team</p>'),
(49, 'deposit_payment_user_email_subject', 'Payment for registration #[REGISTRATION_ID] confirmation'),
(50, 'deposit_payment_user_email_body', '<p>Dear <strong>[FIRST_NAME] [LAST_NAME]</strong></p>\r\n<p>Thanks for completing payment for your registration #<strong>[REGISTRATION_ID]</strong>. The amount you paid is <strong>[AMOUNT] </strong>and the Transaction ID is <strong>[TRANSACTION_ID]</strong></p>\r\n<p>Regards,</p>\r\n<p>Wesite administrator team</p>'),
(51, 'deposit_payment_reminder_email_subject', 'Deposit Payment Reminder'),
(52, 'deposit_payment_reminder_email_body', '<p>Dear [FIRST_NAME] [LAST_NAME]</p>\r\n<p>Please access to  <strong>[DEPOSIT_PAYMENT_LINK] </strong>to complete pay for your registration <strong>#[REGISTRATION_ID] </strong>of event [EVENT_TITLE]. The amount you have to pay is <strong>[AMOUNT]</strong></p>\r\n<p>Regards,</p>\r\n<p>Website administrator team</p>'),
(53, 'offline_payment_reminder_email_subject', 'Offline Payment Reminder for event [EVENT_TITLE] registration'),
(54, 'offline_payment_reminder_email_body', '<p>Dear <strong>[FIRST_NAME], [LAST_NAME]</strong></p>
