"use strict";(function($){$(document).ready(function(){$('div.rl_simplecategory').each(function(i,el){var $el=$(el);var func=function(){var new_value=$(this).val();if(new_value=='-1'){$el.find('.rl_simplecategory_value').val($el.find('.rl_simplecategory_new input').val());return;}
$el.find('.rl_simplecategory_value').val(new_value);};$el.find('.rl_simplecategory_select select').on('change',func).on('keyup',func);$el.find('.rl_simplecategory_new input').on('change',func).on('keyup',func);});});})(jQuery);